import React from "react";
import FavoritesContext from '../../store/Context/context';
import classes from './Video.module.css'
import Card from "../ui/Card";

import { useContext } from 'react';


const Video= () => {
  const favoritesCtx = useContext(FavoritesContext);
  const video = favoritesCtx.videoDirect;

  const videoSrc = `https://www.youtube.com/embed/${video.id.videoId}/?autoplay=1`;
  return (
    <Card>
        <div className={classes.image}>
        <iframe className={classes.player}  src={videoSrc} allowFullScreen title="Video player" />  
        </div>
        <div className={classes.content}>
          <h3>{video.snippet.title}</h3>
          <div className={classes.details}><p><b>Chaine :</b> {video.snippet.channelTitle}</p> 
                <p><b>Date :</b> {video.snippet.publishedAt.substring(0,9)}</p></div>
         
        </div>
        <div className={classes.actions}>
          <button onClick={toggleFavoriteStatusHandler}>
            {itemIsFavorite ? 'supprimer de favoris' : 'Ajouter au favoris'}
          </button>
          <button onClick={()=> regardeVideoHandler(video)}>regarde le video </button>
        </div>
      </Card>
       
  );
};

export default Video;
