import { useState, useEffect, useContext } from "react";
import axios from "axios";
import VideoList from "../components/videos/VideoList";
import FavoritesContext from "../store/favorites-context";
import Spinner from "../components/ui/Spinner";

const KEY = "AIzaSyAnFykwz8PKOIHh3VVCCb7cCdZj9hdFBDs";

const youtube = axios.create({
  baseURL: "https://www.googleapis.com/youtube/v3/",
  params: {
    part: "snippet",
    maxResults: 5,
    key: KEY,
  },
});

function TousLesVideos() {
  const favoritesCtx = useContext(FavoritesContext);

  const [isLoading, setIsLoading] = useState(true);
  const [loadedvideos, setLoadedvideos] = useState([]);
  const [isScrolling, setScrolling] = useState(false);
  const [pageSuivant, setPageSuivant] = useState("");
  const recherche = favoritesCtx.recherche;

  useEffect(() => {
    setIsLoading(true);
    youtube
      .get("/search", {
        params: { q: recherche, pageToken: isScrolling ? pageSuivant : null },
      })
      .then((response) => {
        return response.data;
      })
      .then((data) => {
        const videos = [];
        for (const key in data.items) {
          const video = {
            key: key,
            ...data.items[key],
          };
          //
          videos.push(video);
        }
        setIsLoading(false);
        //au cas du scrolling on ajoute les nouveaux videos par concat
        isScrolling
          ? setLoadedvideos((prevVideos) => prevVideos.concat(videos))
          : // cas du recherche
            setLoadedvideos(videos);
      });
  }, [recherche, isScrolling]);

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <section>
      <h1>All videos</h1>
      <VideoList videos={loadedvideos} />
    </section>
  );
}

export default TousLesVideos;
