import { useContext } from 'react';
import { useHistory } from "react-router-dom";
import Card from '../ui/Card';
import classes from './videoItem.module.css';
import FavoritesContext from '../../store/favorites-context';

function MeetupItem(props) {
  const favoritesCtx = useContext(FavoritesContext);

  const itemIsFavorite = favoritesCtx.itemIsFavorite(props.video.id);

  function toggleFavoriteStatusHandler() {
    if (itemIsFavorite) {
      favoritesCtx.removeFavorite(props.video.id);
    } else {
      favoritesCtx.addFavorite({
        id: props.video.id,
        title: props.video.title,
        description: props.video.description,
        image: props.video.image,
        address: props.video.address,
      });
    }
  } 

  console.log(video)

  let history = useHistory();

  function handleClick() {
    history.push("/home");
  }

  return (
    <li className={classes.item}>
      <Card>
        <div className={classes.image}>
          <img src={props.video.image} alt={props.video.title} />
        </div>
        <div className={classes.content}>
          <h3>{props.video.title}</h3>
          <address>{props.video.address}</address>
          <p>{props.video.description}</p>
        </div>
        <div className={classes.actions}>
          <button onClick={toggleFavoriteStatusHandler}>
            {itemIsFavorite ? 'supprimer de favoris' : 'Ajouter au favoris'}
          </button>
          <button onClick={handleClick}>Regarder le video</button>
        </div>
      </Card>
    </li>
  );
}

export default MeetupItem;
