import { createSlice } from '@reduxjs/toolkit'

export const rechercheSlice = createSlice({
  name: 'recherche',
  initialState: {
    recherche: 'paris',
  },
  reducers: {
    setRecherche: (state ,action) => {
      state.recherche = action;
    }
  }
})

export const { setRecharche } = rechercheSlice.actions

export default rechercheSlice.reducer
