import { useContext ,useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux'
import classes from './MainNavigation.module.css';
import FavoritesContext from '../../store/favorites-context';
import { rechercheSlice} from '../../store/redux/rechercheSlice'

function MainNavigation() {
  const favoritesCtx = useContext(FavoritesContext);
  const dispatch = useDispatch();

  const handleChange = (event) => {
     dispatch(rechercheSlice())
  }

  return (
    <header className={classes.header}>
      <div className={classes.logo}>Youtube <img src='youtube.png' alt='logo'/> </div>
      <input className={classes.rech} onChange={handleChange} type="text" placeholder="recherche.."/>
      <nav>
        <ul>
          <li>
            <Link to='/'>Tous les Videos</Link>
          </li>
          <li>
            <Link to='/favoris'>
            Mes favoris
              <span className={classes.badge}>
                {favoritesCtx.totalFavorites}
              </span>
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default MainNavigation;
